import SPagebar from '@/layout/SPagebar.vue';
import * as documentoService from '@/services/documento.service';
export default {
  name: 'DocumentoList',
  components: {
    SPagebar
  },
  data() {
    return {
      breadcrumbs: [{
        text: 'Relatórios',
        to: '/relatorio',
        exact: true
      }],
      relatorio: {
        tipo_relatorio: '',
        categoria: '',
        ponto_numero: ''
      },
      errors: {},
      tiposRelatorio: [{
        text: 'Auxiliar',
        value: 'auxiliar',
        categorias: ['turismo', 'escolar', 'taxi']
      }, {
        text: 'Acompanhante',
        value: 'acompanhante',
        categorias: ['escolar']
      }, {
        text: 'Condutor',
        value: 'condutor',
        categorias: ['fretamento']
      }, {
        text: 'Cooperado',
        value: 'cooperado',
        categorias: ['turismo']
      }, {
        text: 'Arrendador',
        value: 'arrendador',
        categorias: ['turismo', 'fretamento']
      }, {
        text: "Idade Média",
        value: "idade"
      }, {
        text: "Infrações",
        value: "infracoes"
      }, {
        text: "Permissionários",
        value: "permissionario"
      }, {
        text: "Pontos",
        value: "pontos",
        categorias: ['taxi']
      }],
      loading: false
    };
  },
  computed: {
    tiposRelatorioFiltrados() {
      if (!this.relatorio.categoria) return [];
      return this.tiposRelatorio.filter(tipo => {
        return !tipo.categorias || tipo.categorias.includes(this.relatorio.categoria);
      });
    }
  },
  methods: {
    salvar() {
      this.loading = true;
      let request = documentoService.carregarArquivo({
        resource: this.$endpoints.RELATORIO,
        data: this.relatorio
      });
      request.then(response => {
        this.loading = false;
        const downloadUrl = window.URL.createObjectURL(response.data);
        window.open(downloadUrl, '__blank');
      }).catch(error => {
        this.handleBlobError(error);
        this.loading = false;
      });
    }
  }
};
