import SPagebar from '@/layout/SPagebar.vue';
export default {
  name: 'TaxaCreate',
  components: {
    SPagebar
  },
  data: () => ({
    breadcrumbs: [{
      'text': 'Taxas',
      'to': '/taxa',
      'exact': true
    }, {
      'text': 'Cadastrar',
      'disabled': true
    }],
    categorias: [{
      text: 'Táxi',
      value: 'taxi'
    }, {
      text: 'Escolar',
      value: 'escolar'
    }, {
      text: 'Turismo',
      value: 'turismo'
    }, {
      text: 'Fretamento',
      value: 'fretamento'
    }],
    taxa: {},
    errors: {}
  }),
  methods: {
    salvar() {
      const response = this.$api.create({
        resource: this.$endpoints.TAXA,
        data: this.taxa
      });
      response.then(() => {
        this.$toast.open({
          message: 'Registro salvo com sucesso',
          type: 'success'
        });
        this.$router.back();
      }).catch(error => this.errors = this.handleError(error));
    }
  }
};
