import SPagebar from '@/layout/SPagebar.vue';
export default {
  name: 'TaxaCreate',
  components: {
    SPagebar
  },
  data: () => ({
    breadcrumbs: [{
      'text': 'Taxas',
      'to': '/taxa',
      'exact': true
    }, {
      'text': 'Editar',
      'disabled': true
    }],
    categorias: [{
      text: 'Táxi',
      value: 'taxi'
    }, {
      text: 'Escolar',
      value: 'escolar'
    }, {
      text: 'Turismo',
      value: 'turismo'
    }, {
      text: 'Fretamento',
      value: 'fretamento'
    }],
    taxa: {},
    errors: {}
  }),
  created() {
    this.getTaxa(this.$route.params.id);
  },
  methods: {
    async getTaxa(id) {
      const response = await this.$api.get({
        resource: this.$endpoints.TAXA,
        id: id
      });
      this.taxa = response.data;
    },
    salvar() {
      const response = this.$api.update({
        resource: this.$endpoints.TAXA,
        id: this.taxa.id,
        data: this.taxa
      });
      response.then(() => {
        this.$toast.open({
          message: 'Registro salvo com sucesso',
          type: 'success'
        });
        this.$router.back();
      }).catch(error => this.errors = this.handleError(error));
    }
  }
};
