import SPagebar from '@/layout/SPagebar.vue';
import * as SemobService from '@/services/semob.service';
import PromptDialog from '@/components/PromptDialog.vue';
export default {
  name: 'VeiculoConsulta',
  components: {
    SPagebar,
    PromptDialog
  },
  data: () => ({
    carregando: false,
    breadcrumbs: [{
      'text': 'Consultar veículo',
      'disabled': true
    }],
    categoria: null,
    veiculo: {
      placa: '',
      uf: '',
      proprietario: '',
      municipio: '',
      chassi: '',
      remarcacao_chassi: '',
      desc_tipo: '',
      desc_especie: '',
      desc_marca_modelo: '',
      desc_cor: '',
      ano_fabricacao: '',
      ano_modelo: '',
      desc_carroceria: '',
      desc_combustivel: '',
      desc_categoria: '',
      status: '',
      licenciamento: '',
      restricoes: '',
      capacidade: ''
    },
    errors: {}
  }),
  methods: {
    consultarVeiculo(placa) {
      if (placa && placa.length == 7) {
        this.carregando = true;
        SemobService.buscarVeiculo({
          placa: placa
        }).then(response => {
          Object.assign(this.veiculo, response.data);
          if (Array.isArray(response.data.restricoes)) {
            this.veiculo.restricoes = response.data.restricoes.join(', ');
          }
        }).catch(err => {
          this.$toast.open({
            message: err.response.data.detail,
            type: 'error'
          });
        }).finally(() => {
          this.carregando = false;
        });
      }
    }
  }
};
