    breadcrumbs: [{
      'text': 'Permissões',
      'to': '/permissao',
      'exact': true
    }, {
      'text': 'Detalhes',
      'disabled': true
    }, {
      'text': 'Incluir veículo',
      'disabled': true
    }],
    categoria: null,
    veiculo: {
      placa: '',
      uf: '',
      proprietario: '',
      municipio: '',
      chassi: '',
      remarcacao_chassi: '',
      desc_tipo: '',
      desc_especie: '',
      desc_marca_modelo: '',
      desc_cor: '',
      ano_fabricacao: '',
      ano_modelo: '',
      desc_carroceria: '',
      desc_combustivel: '',
      desc_categoria: '',
      status: '',
      licenciamento: '',
      restricoes: '',
      capacidade: '',
      buggy: ''
    },
    errors: {}
  }),
  created() {
    this.veiculo.permissao = this.$route.query.permissao;
    this.getPermissao();
  },
  methods: {
    handleFileUpload(val) {
      this.processo.documento = val;
    },
    async getPermissao() {
      const response = await this.$api.get({
        resource: this.$endpoints.PERMISSAO,
        id: this.veiculo.permissao
      });
      this.categoria = response.data.categoria;
    },
    async salvar() {
      if (this.veiculo.desc_carroceria.length >= 20) {
        this.veiculo.desc_carroceria = this.veiculo.desc_carroceria.substring(0, 20);
      }
      const response = this.$api.create({
        resource: this.$endpoints.VEICULO,
        data: this.veiculo
      });
      response.then(() => {
        this.$toast.open({
          message: 'Registro salvo com sucesso',
          type: 'success'
        });
        this.$router.back();
      }).catch(error => {
        this.errors = this.handleError(error);
      });
    }
  }
};
