import SPagebar from '@/layout/SPagebar.vue';
import * as SemobService from '@/services/semob.service';
export default {
  name: 'VeiculoCreate',
  components: {
    SPagebar
  },
  data: () => ({
    carregando: false,
    breadcrumbs: [{
      'text': 'Permissões',
      'to': '/permissao',
      'exact': true
    }, {
      'text': 'Detalhes',
      'disabled': true
    }, {
      'text': 'Incluir veículo',
      'disabled': true
    }],
    categoria: null,
    veiculo: {
      placa: '',
      uf: '',
      proprietario: '',
      municipio: '',
      chassi: '',
      remarcacao_chassi: '',
      desc_tipo: '',
      desc_especie: '',
      desc_marca_modelo: '',
      desc_cor: '',
      ano_fabricacao: '',
      ano_modelo: '',
      desc_carroceria: '',
      desc_combustivel: '',
      desc_categoria: '',
      status: '',
      licenciamento: '',
      restricoes: '',
      capacidade: '',
      pre_cadastro: false
    },
    errors: {}
  }),
  created() {
    this.veiculo.permissao = this.$route.query.permissao;
    this.getPermissao();
  },
  methods: {
    async getPermissao() {
      const response = await this.$api.get({
        resource: this.$endpoints.PERMISSAO,
        id: this.veiculo.permissao
      });
      this.categoria = response.data.categoria;
    },
    salvar() {
      this.veiculo.pre_cadastro = true;
      const response = this.$api.create({
        resource: this.$endpoints.VEICULO,
        data: this.veiculo
      });
      response.then(() => {
        this.$toast.open({
          message: 'Registro salvo com sucesso',
          type: 'success'
        });
        this.$router.back();
      }).catch(error => {
        this.errors = this.handleError(error);
      });
    },
    consultarVeiculoPorPlaca(placa) {
      if (placa && placa.length == 7) {
        this.carregando = true;
        SemobService.buscarVeiculo({
          placa: placa
        }).then(response => {
          Object.assign(this.veiculo, response.data);
          if (Array.isArray(response.data.restricoes)) {
            this.veiculo.restricoes = response.data.restricoes.join(', ');
          }
          this.carregando = false;
        });
      }
    },
    consultarVeiculoPorChassi(chassi) {
      if (chassi && chassi.length == 17) {
        this.carregando = true;
        SemobService.buscarVeiculo({
          chassi: chassi
        }).then(response => {
          Object.assign(this.veiculo, response.data);
          if (Array.isArray(response.data.restricoes)) {
            this.veiculo.restricoes = response.data.restricoes.join(', ');
          }
          this.carregando = false;
        });
      }
    }
  }
};
