import SPagebar from '@/layout/SPagebar.vue';
export default {
  name: 'RequerimentoCreate',
  components: {
    SPagebar
  },
  data: () => ({
    breadcrumbs: [{
      'text': 'Requerimentos',
      'to': '/requerimentos',
      'exact': true
    }, {
      'text': 'Cadastrar',
      'disabled': true
    }],
    requerimento: {},
    errors: {},
    permissao: {},
    descricoes: [],
    status: [{
      text: 'Em andamento',
      value: 'em_andamento'
    }, {
      text: 'Cancelado',
      value: 'cancelado'
    }, {
      text: 'Finalizado',
      value: 'finalizado'
    }],
    categorias: [{
      text: 'Táxi',
      value: 'taxi'
    }, {
      text: 'Escolar',
      value: 'escolar'
    }, {
      text: 'Turismo',
      value: 'turismo'
    }, {
      text: 'Fretamento',
      value: 'fretamento'
    }]
  }),
  created() {
    this.getRequerimento(this.$route.params.id);
  },
  methods: {
    async getRequerimento(id) {
      const response = await this.$api.get({
        resource: this.$endpoints.PROCESSO,
        id: id
      });
      this.requerimento = response.data;
      this.getPermissao(this.requerimento.permissao);
    },
    async getPermissao(id) {
      const response = await this.$api.get({
        resource: this.$endpoints.PERMISSAO,
        id: id
      });
      this.permissao = response.data;
      this.getDescricoes();
    },
    async getDescricoes() {
      const response = await this.$api.list({
        resource: this.$endpoints.PROCESSO_DESCRICAO,
        query: {
          page_size: 10000,
          categoria: this.permissao.categoria
        }
      });
      this.descricoes = response.data.results;
    },
    salvar() {
      this.requerimento.placa = this.requerimento.placa.toUpperCase();
      let formData = new FormData();
      formData.append('id', this.requerimento.id);
      formData.append('permissao', this.permissao.id);
      formData.append('numero', this.requerimento.numero);
      formData.append('placa', this.requerimento.placa);
      formData.append('categoria', this.permissao.categoria);
      formData.append('descricao', this.requerimento.descricao);
      formData.append('documento', this.requerimento.documento);
      const response = this.$api.update({
        resource: this.$endpoints.PROCESSO,
        id: this.requerimento.id,
        data: formData
      });
      response.then(() => {
        this.$toast.open({
          message: 'Registro salvo com sucesso',
          type: 'success'
        });
        this.$router.back();
      }).catch(error => this.errors = this.handleError(error));
    },
    handleFile(val) {
      this.requerimento.documento = val;
    }
  }
};
