import SPagebar from '@/layout/SPagebar.vue';
import * as SemobService from '@/services/semob.service';
import PromptDialog from '@/components/PromptDialog.vue';
export default {
  name: 'VeiculoEdit',
  components: {
    SPagebar,
    PromptDialog
  },
  data: () => ({
    carregando: false,
    breadcrumbs: [{
      'text': 'Permissões',
      'to': '/permissao',
      'exact': true
    }, {
      'text': 'Detalhes',
      'disabled': true
    }, {
      'text': 'Editar veículo',
      'disabled': true
    }],
    veiculo: {
      placa: '',
      uf: '',
      proprietario: '',
      municipio: '',
      chassi: '',
      remarcacao_chassi: '',
      desc_tipo: '',
      desc_especie: '',
      desc_marca_modelo: '',
      desc_cor: '',
      ano_fabricacao: '',
      ano_modelo: '',
      desc_carroceria: '',
      desc_combustivel: '',
      desc_categoria: '',
      status: '',
      licenciamento: '',
      restricoes: '',
      capacidade: ''
    },
    errors: {}
  }),
  created() {
    this.getVeiculo(this.$route.params.id);
  },
  methods: {
    async getVeiculo(id) {
      const response = await this.$api.get({
        resource: this.$endpoints.VEICULO,
        id: id
      });
      this.veiculo = response.data;
    },
    async salvar() {
      const res = await this.$refs.prompt.open('Requerimento', 'Informe o número do requerimento', 'Número do requerimento');
      if (res) {
        this.veiculo.numero_requerimento = res;
        const response = this.$api.update({
          resource: this.$endpoints.VEICULO,
          id: this.veiculo.id,
          data: this.veiculo
        });
        response.then(() => {
          this.$toast.open({
            message: 'Registro salvo com sucesso',
            type: 'success'
          });
          this.$router.back();
        }).catch(error => {
          this.errors = this.handleError(error);
        });
      }
    },
    consultarVeiculo(placa) {
      if (placa && placa.length == 7) {
        this.carregando = true;
        SemobService.buscarVeiculo({
          placa: placa
        }).then(response => {
          Object.assign(this.veiculo, response.data);
          if (Array.isArray(response.data.restricoes)) {
            this.veiculo.restricoes = response.data.restricoes.join(', ');
          }
        }).catch(err => {
          this.$toast.open({
            message: err.response.data.detail,
            type: 'error'
          });
        }).finally(() => {
          this.carregando = false;
        });
      }
    },
    async atualizarAlvara() {
      const [dia, mes, ano] = this.veiculo.alvara_validade.split('/').map(Number);
      const novaDataValidade = new Date(ano, mes - 1, dia + 365);
      const novaDataValidadeFormatada = novaDataValidade.toLocaleDateString('pt-BR');
      const confirmMessage = `Tem certeza que deseja atualizar o alvará?\n
          Data de validade atual: ${this.veiculo.alvara_validade}\n
          Nova data de validade: ${novaDataValidadeFormatada}`;
      const res = window.confirm(confirmMessage);
      if (res) {
        const response = this.$api.create({
          resource: this.$endpoints.ALVARA,
          id: this.veiculo.id,
          data: this.veiculo
        });
        response.then(() => {
          this.$toast.open({
            message: 'Alvará atualizado com sucesso',
            type: 'success'
          });
          this.$router.back();
        }).catch(error => {
          this.errors = this.handleError(error);
        });
      }
    }
  }
};
