import SPagebar from '@/layout/SPagebar.vue';
export default {
  name: 'PontoCreate',
  components: {
    SPagebar
  },
  data: () => ({
    breadcrumbs: [{
      'text': 'Pontos',
      'to': '/ponto',
      'exact': true
    }, {
      'text': 'Cadastrar',
      'disabled': true
    }],
    ponto: {},
    errors: {}
  }),
  methods: {
    salvar() {
      const response = this.$api.create({
        resource: this.$endpoints.PONTO,
        data: this.ponto
      });
      response.then(() => {
        this.$toast.open({
          message: 'Registro salvo com sucesso',
          type: 'success'
        });
        this.$router.back();
      }).catch(error => this.errors = this.handleError(error));
    }
  }
};
